import React, { useState } from 'react';
import { Language, getTranslation } from './languages';

interface LoginProps {
  onLogin: (email: string, password: string) => void;
  isLoading: boolean;
  error: string;
  currentLanguage: Language;
}

const Login: React.FC<LoginProps> = ({ onLogin, isLoading, error, currentLanguage }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const t = (key: string) => getTranslation(currentLanguage.code, key);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onLogin(email, password);
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <h1>{t('loginTitle')}</h1>
          <p>{t('loginSubtitle')}</p>
        </div>

        <form className="login-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">{t('email')}</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder={`Enter your ${t('email').toLowerCase()}`}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">{t('password')}</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder={`Enter your ${t('password').toLowerCase()}`}
              required
            />
          </div>

          <button
            type="submit"
            className="login-btn"
            disabled={isLoading || !email || !password}
          >
            {isLoading ? t('signingIn') : t('signIn')}
          </button>

          {error && (
            <div className="error-message">
              {error}
            </div>
          )}
        </form>

        <div className="demo-credentials">
          <p>{t('demoCredentials')}</p>
        </div>
      </div>
    </div>
  );
};

export default Login;
