import React, { useState, useRef } from 'react';
import axios from 'axios';
import ReactMarkdown from 'react-markdown';
import Login from './Login';
import LanguageSelector from './LanguageSelector';
import Settings from './Settings';
import { languages, Language, getTranslation } from './languages';
import './App.css';

interface Message {
  id: string;
  text: string;
  isUser: boolean;
  timestamp: Date;
  image?: string;
  isPrescription?: boolean;
}

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoginLoading, setIsLoginLoading] = useState(false);
  const [loginError, setLoginError] = useState('');
  const [showLanguageSelector, setShowLanguageSelector] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [currentLanguage, setCurrentLanguage] = useState<Language>(languages[0]);
  const [currentEmail, setCurrentEmail] = useState('admin@pediatric.ai');
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputText, setInputText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [selectedImage, setSelectedImage] = useState<File | null>(null);
  const [showPrescriptionForm, setShowPrescriptionForm] = useState(false);
  const [patientAge, setPatientAge] = useState('');
  const [patientConditions, setPatientConditions] = useState('');
  const [isPrescriptionLoading, setIsPrescriptionLoading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const t = (key: string) => getTranslation(currentLanguage.code, key);

  const handleLogin = async (email: string, password: string) => {
    setIsLoginLoading(true);
    setLoginError('');
    await new Promise(resolve => setTimeout(resolve, 1000));
    if (email === 'admin@pediatric.ai' && password === 'password123') {
      setIsAuthenticated(true);
      setCurrentEmail(email);
      setLoginError('');
      setShowLanguageSelector(true);
    } else {
      setLoginError('Invalid credentials. Please try again.');
    }
    setIsLoginLoading(false);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setMessages([]);
    setInputText('');
    setSelectedImage(null);
    setShowPrescriptionForm(false);
    setShowLanguageSelector(false);
    setShowSettings(false);
  };

  const handleLanguageSelect = (language: Language) => {
    setCurrentLanguage(language);
    setShowLanguageSelector(false);
  };

  const handleEmailChange = (newEmail: string) => {
    setCurrentEmail(newEmail);
  };

  const handlePasswordChange = (currentPassword: string, newPassword: string) => {
    // In a real app, this would make an API call to change the password
    console.log('Password changed:', { currentPassword, newPassword });
  };

  const sendMessage = async () => {
    if (!inputText.trim() && !selectedImage) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      text: inputText,
      isUser: true,
      timestamp: new Date(),
      image: selectedImage ? URL.createObjectURL(selectedImage) : undefined
    };

    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setSelectedImage(null);
    setIsLoading(true);

    try {
      const formData = new FormData();
      formData.append('message', inputText);
      if (selectedImage) {
        formData.append('image', selectedImage);
      }

      const response = await axios.post('http://localhost:8000/chat/', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      const aiMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: response.data.response,
        isUser: false,
        timestamp: new Date(),
        isPrescription: response.data.is_prescription_query
      };

      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: 'Sorry, there was an error processing your request. Please try again.',
        isUser: false,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const analyzePrescription = async () => {
    if (!selectedImage) return;

    setIsPrescriptionLoading(true);

    try {
      const formData = new FormData();
      formData.append('image', selectedImage);
      if (patientAge) formData.append('patient_age', patientAge);
      if (patientConditions) formData.append('patient_conditions', patientConditions);

      const response = await axios.post('http://localhost:8000/prescription-analysis/', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      const userMessage: Message = {
        id: Date.now().toString(),
        text: `${t('prescriptionAnalysis')} Request${patientAge ? ` (${t('patientAge')}: ${patientAge})` : ''}${patientConditions ? ` (${t('medicalConditions')}: ${patientConditions})` : ''}`,
        isUser: true,
        timestamp: new Date(),
        image: URL.createObjectURL(selectedImage),
        isPrescription: true
      };

      const aiMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: response.data.response,
        isUser: false,
        timestamp: new Date(),
        isPrescription: true
      };

      setMessages(prev => [...prev, userMessage, aiMessage]);
      setShowPrescriptionForm(false);
      setSelectedImage(null);
      setPatientAge('');
      setPatientConditions('');
    } catch (error) {
      console.error('Error analyzing prescription:', error);
      const errorMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: 'Sorry, there was an error analyzing the prescription. Please try again or consult with a pharmacist.',
        isUser: false,
        timestamp: new Date(),
        isPrescription: true
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsPrescriptionLoading(false);
    }
  };

  const handleImageSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedImage(file);
    }
  };

  const removeSelectedImage = () => {
    setSelectedImage(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const handleKeyPress = (event: React.KeyboardEvent) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      sendMessage();
    }
  };

  if (!isAuthenticated) {
    return (
      <Login
        onLogin={handleLogin}
        isLoading={isLoginLoading}
        error={loginError}
        currentLanguage={currentLanguage}
      />
    );
  }

  return (
    <div className="App">
      <LanguageSelector
        isVisible={showLanguageSelector}
        onLanguageSelect={handleLanguageSelect}
      />

      <Settings
        isVisible={showSettings}
        onClose={() => setShowSettings(false)}
        currentLanguage={currentLanguage}
        onLanguageChange={setCurrentLanguage}
        currentEmail={currentEmail}
        onEmailChange={handleEmailChange}
        onPasswordChange={handlePasswordChange}
      />

      <div className="chat-container">
        <div className="chat-header">
          <div className="header-content">
            <div className="header-info">
              <h1>🩺 {t('loginTitle')}</h1>
              <p>{t('welcome')}</p>
            </div>
            <div className="header-actions">
              <button
                onClick={() => setShowPrescriptionForm(!showPrescriptionForm)}
                className="prescription-btn"
              >
                📋 {t('prescriptionAnalysis')}
              </button>
              <button
                onClick={() => setShowSettings(true)}
                className="settings-btn"
              >
                ⚙️ {t('settings')}
              </button>
              <button
                onClick={handleLogout}
                className="logout-btn"
              >
                {t('logout')}
              </button>
            </div>
          </div>
        </div>

        <div className="messages-container">
          {messages.map((message) => (
            <div key={message.id} className={`message ${message.isUser ? 'user' : 'ai'}`}>
              <div className="message-content">
                {message.image && (
                  <img
                    src={message.image}
                    alt="Uploaded"
                    className="message-image"
                  />
                )}
                                 <div className="message-text">
                   <div className="markdown-content">
                     <ReactMarkdown>
                       {message.text}
                     </ReactMarkdown>
                   </div>
                 </div>
              </div>
            </div>
          ))}
          {isLoading && (
            <div className="message ai">
              <div className="message-content">
                <p>{t('thinking')}</p>
              </div>
            </div>
          )}
          {isPrescriptionLoading && (
            <div className="message ai">
              <div className="message-content">
                <p>{t('analyzing')}</p>
              </div>
            </div>
          )}
        </div>

        {showPrescriptionForm && (
          <div className="prescription-form">
            <h3>📋 {t('prescriptionAnalysis')}</h3>
            <div className="prescription-inputs">
              <div className="input-group">
                <label>{t('patientAge')}</label>
                <input
                  type="text"
                  value={patientAge}
                  onChange={(e) => setPatientAge(e.target.value)}
                  placeholder={t('patientAgePlaceholder')}
                />
              </div>
              <div className="input-group">
                <label>{t('medicalConditions')}</label>
                <input
                  type="text"
                  value={patientConditions}
                  onChange={(e) => setPatientConditions(e.target.value)}
                  placeholder={t('medicalConditionsPlaceholder')}
                />
              </div>
            </div>
            <div className="prescription-actions">
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleImageSelect}
                accept="image/*"
                style={{ display: 'none' }}
              />
              <button
                onClick={() => fileInputRef.current?.click()}
                className="upload-btn"
              >
                📷 {t('uploadPrescription')}
              </button>
              {selectedImage && (
                <button
                  onClick={analyzePrescription}
                  disabled={isPrescriptionLoading}
                  className="analyze-btn"
                >
                  {isPrescriptionLoading ? t('analyzing') : t('analyzePrescription')}
                </button>
              )}
            </div>
            {selectedImage && (
              <div className="selected-image">
                <img
                  src={URL.createObjectURL(selectedImage)}
                  alt="Selected"
                  className="preview-image"
                />
                <button
                  onClick={removeSelectedImage}
                  className="remove-btn"
                >
                  ✕
                </button>
              </div>
            )}
          </div>
        )}

        <div className="input-container">
          <div className="input-wrapper">
            <textarea
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder={t('chatPlaceholder')}
              className="text-input"
              rows={3}
            />
            <div className="input-actions">
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleImageSelect}
                accept="image/*"
                style={{ display: 'none' }}
              />
              <button
                onClick={() => fileInputRef.current?.click()}
                className="upload-btn"
                title={t('uploadImage')}
              >
                📷
              </button>
              <button
                onClick={sendMessage}
                disabled={isLoading || (!inputText.trim() && !selectedImage)}
                className="send-btn"
              >
                {t('send')}
              </button>
            </div>
          </div>
          {selectedImage && !showPrescriptionForm && (
            <div className="selected-image">
              <img
                src={URL.createObjectURL(selectedImage)}
                alt="Selected"
                className="preview-image"
              />
              <button onClick={removeSelectedImage} className="remove-btn">
                ✕
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
