import React from 'react';
import { languages, Language } from './languages';

interface LanguageSelectorProps {
  onLanguageSelect: (language: Language) => void;
  isVisible: boolean;
}

const LanguageSelector: React.FC<LanguageSelectorProps> = ({ onLanguageSelect, isVisible }) => {
  if (!isVisible) return null;

  return (
    <div className="language-selector-overlay">
      <div className="language-selector-modal">
        <div className="language-selector-header">
          <h2>üåç Select Your Language</h2>
          <p>Choose your preferred language for the app</p>
        </div>

        <div className="language-grid">
          {languages.map((language) => (
            <button
              key={language.code}
              className="language-option"
              onClick={() => onLanguageSelect(language)}
            >
              <div className="language-info">
                <div className="language-name">{language.name}</div>
                <div className="language-native">{language.nativeName}</div>
              </div>
              <div className="language-arrow">‚Üí</div>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default LanguageSelector;
