import React, { useState, useRef } from 'react';
import axios from 'axios';
import ReactMarkdown from 'react-markdown';
import Login from './Login';
import './App.css';

interface Message {
  id: string;
  text: string;
  isUser: boolean;
  timestamp: Date;
  image?: string;
}

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoginLoading, setIsLoginLoading] = useState(false);
  const [loginError, setLoginError] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputText, setInputText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [selectedImage, setSelectedImage] = useState<File | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleLogin = async (email: string, password: string) => {
    setIsLoginLoading(true);
    setLoginError('');

    // Simulate login delay
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Simple demo authentication
    if (email === 'admin@pediatric.ai' && password === 'password123') {
      setIsAuthenticated(true);
      setLoginError('');
    } else {
      setLoginError('Invalid credentials. Please try again.');
    }

    setIsLoginLoading(false);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setMessages([]);
    setInputText('');
    setSelectedImage(null);
  };

  const sendMessage = async () => {
    if (!inputText.trim() && !selectedImage) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      text: inputText,
      isUser: true,
      timestamp: new Date(),
      image: selectedImage ? URL.createObjectURL(selectedImage) : undefined
    };

    setMessages(prev => [...prev, userMessage]);
    setIsLoading(true);

    try {
      const formData = new FormData();
      formData.append('message', inputText);
      if (selectedImage) {
        formData.append('image', selectedImage);
      }

      const response = await axios.post('http://localhost:8000/chat/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      const aiMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: response.data.response,
        isUser: false,
        timestamp: new Date(),
      };

      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: 'Sorry, there was an error processing your request. Please try again.',
        isUser: false,
        timestamp: new Date(),
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
      setInputText('');
      setSelectedImage(null);
    }
  };

  const handleImageSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedImage(file);
    }
  };

  const removeSelectedImage = () => {
    setSelectedImage(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const handleKeyPress = (event: React.KeyboardEvent) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      sendMessage();
    }
  };

  if (!isAuthenticated) {
    return (
      <Login 
        onLogin={handleLogin}
        isLoading={isLoginLoading}
        error={loginError}
      />
    );
  }

  return (
    <div className="App">
      <div className="chat-container">
        <div className="chat-header">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div>
              <h1>ðŸ©º Pediatric AI Assistant</h1>
              <p>Ask me about your child's health concerns</p>
            </div>
            <button 
              onClick={handleLogout}
              style={{
                background: 'rgba(255, 255, 255, 0.1)',
                border: '1px solid rgba(255, 255, 255, 0.2)',
                color: 'white',
                padding: '8px 16px',
                borderRadius: '8px',
                cursor: 'pointer',
                fontSize: '12px',
                transition: 'all 0.2s'
              }}
              onMouseOver={(e) => {
                e.currentTarget.style.background = 'rgba(255, 255, 255, 0.2)';
              }}
              onMouseOut={(e) => {
                e.currentTarget.style.background = 'rgba(255, 255, 255, 0.1)';
              }}
            >
              Logout
            </button>
          </div>
        </div>

        <div className="messages-container">
          {messages.map((message) => (
            <div key={message.id} className={`message ${message.isUser ? 'user' : 'ai'}`}>
              <div className="message-content">
                {message.isUser ? (
                  <p>{message.text}</p>
                ) : (
                  <div className="markdown-content">
                    <ReactMarkdown 
                      components={{
                        h1: ({node, ...props}) => <h1 style={{fontSize: '1.2em', margin: '8px 0'}} {...props} />,
                        h2: ({node, ...props}) => <h2 style={{fontSize: '1.1em', margin: '8px 0'}} {...props} />,
                        h3: ({node, ...props}) => <h3 style={{fontSize: '1em', margin: '6px 0'}} {...props} />,
                        p: ({node, ...props}) => <p style={{margin: '4px 0'}} {...props} />,
                        ul: ({node, ...props}) => <ul style={{margin: '8px 0', paddingLeft: '20px'}} {...props} />,
                        ol: ({node, ...props}) => <ol style={{margin: '8px 0', paddingLeft: '20px'}} {...props} />,
                        li: ({node, ...props}) => <li style={{margin: '2px 0'}} {...props} />,
                        strong: ({node, ...props}) => <strong style={{fontWeight: 'bold'}} {...props} />,
                        em: ({node, ...props}) => <em style={{fontStyle: 'italic'}} {...props} />,
                        blockquote: ({node, ...props}) => (
                          <blockquote style={{
                            borderLeft: '3px solid #667eea',
                            paddingLeft: '12px',
                            margin: '8px 0',
                            fontStyle: 'italic'
                          }} {...props} />
                        ),
                        code: ({node, ...props}) => (
                          <code style={{
                            backgroundColor: 'rgba(102, 126, 234, 0.1)',
                            padding: '2px 4px',
                            borderRadius: '3px',
                            fontFamily: 'monospace'
                          }} {...props} />
                        )
                      }}
                    >
                      {message.text}
                    </ReactMarkdown>
                  </div>
                )}
                {message.image && (
                  <img 
                    src={message.image} 
                    alt="Uploaded" 
                    className="message-image"
                    style={{ maxWidth: '200px', maxHeight: '200px' }}
                  />
                )}
              </div>
              <div className="message-timestamp">
                {message.timestamp.toLocaleTimeString()}
              </div>
            </div>
          ))}
          {isLoading && (
            <div className="message ai">
              <div className="message-content">
                <p>Thinking...</p>
              </div>
            </div>
          )}
        </div>

        <div className="input-container">
          <div className="input-wrapper">
            <textarea
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Describe your child's symptoms or ask a question..."
              className="text-input"
              rows={3}
            />
            <div className="input-actions">
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleImageSelect}
                accept="image/*"
                style={{ display: 'none' }}
              />
              <button 
                onClick={() => fileInputRef.current?.click()}
                className="upload-btn"
                type="button"
              >
                ðŸ“·
              </button>
              <button 
                onClick={sendMessage}
                disabled={isLoading || (!inputText.trim() && !selectedImage)}
                className="send-btn"
              >
                Send
              </button>
            </div>
          </div>

          {selectedImage && (
            <div className="selected-image">
              <img 
                src={URL.createObjectURL(selectedImage)} 
                alt="Selected" 
                style={{ maxWidth: '100px', maxHeight: '100px' }}
              />
              <button onClick={removeSelectedImage} className="remove-image-btn">
                âœ•
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
