import React, { useState } from 'react';
import { languages, Language, getTranslation } from './languages';

interface SettingsProps {
  isVisible: boolean;
  onClose: () => void;
  currentLanguage: Language;
  onLanguageChange: (language: Language) => void;
  currentEmail: string;
  onEmailChange: (email: string) => void;
  onPasswordChange: (currentPassword: string, newPassword: string) => void;
  isDarkMode: boolean;
  onDarkModeToggle: () => void;
}

const Settings: React.FC<SettingsProps> = ({
  isVisible,
  onClose,
  currentLanguage,
  onLanguageChange,
  currentEmail,
  onEmailChange,
  onPasswordChange,
  isDarkMode,
  onDarkModeToggle
}) => {
  const [activeTab, setActiveTab] = useState<'language' | 'appearance' | 'email' | 'password'>('language');
  const [newEmail, setNewEmail] = useState(currentEmail);
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');

  const t = (key: string) => getTranslation(currentLanguage.code, key);

  const handleEmailSave = async () => {
    setIsLoading(true);
    setMessage('');

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      onEmailChange(newEmail);
      setMessage(t('settingsSaved'));
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      setMessage(t('errorOccurred'));
    } finally {
      setIsLoading(false);
    }
  };

  const handlePasswordSave = async () => {
    if (newPassword !== confirmPassword) {
      setMessage('Passwords do not match');
      return;
    }

    setIsLoading(true);
    setMessage('');

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      onPasswordChange(currentPassword, newPassword);
      setCurrentPassword('');
      setNewPassword('');
      setConfirmPassword('');
      setMessage(t('settingsSaved'));
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      setMessage(t('errorOccurred'));
    } finally {
      setIsLoading(false);
    }
  };

  if (!isVisible) return null;

  return (
    <div className="settings-overlay">
      <div className="settings-modal">
        <div className="settings-header">
          <h2>‚öôÔ∏è {t('settings')}</h2>
          <button className="settings-close" onClick={onClose}>‚úï</button>
        </div>

        <div className="settings-tabs">
          <button
            className={`settings-tab ${activeTab === 'language' ? 'active' : ''}`}
            onClick={() => setActiveTab('language')}
          >
            üåç {t('language')}
          </button>
          <button
            className={`settings-tab ${activeTab === 'appearance' ? 'active' : ''}`}
            onClick={() => setActiveTab('appearance')}
          >
            üé® {t('appearance')}
          </button>
          <button
            className={`settings-tab ${activeTab === 'email' ? 'active' : ''}`}
            onClick={() => setActiveTab('email')}
          >
            üìß {t('email')}
          </button>
          <button
            className={`settings-tab ${activeTab === 'password' ? 'active' : ''}`}
            onClick={() => setActiveTab('password')}
          >
            üîí {t('password')}
          </button>
        </div>

        <div className="settings-content">
          {activeTab === 'language' && (
            <div className="settings-section">
              <h3>{t('changeLanguage')}</h3>
              <div className="language-list">
                {languages.map((language) => (
                  <button
                    key={language.code}
                    className={`language-item ${currentLanguage.code === language.code ? 'selected' : ''}`}
                    onClick={() => onLanguageChange(language)}
                  >
                    <div className="language-item-info">
                      <div className="language-item-name">{language.name}</div>
                      <div className="language-item-native">{language.nativeName}</div>
                    </div>
                    {currentLanguage.code === language.code && (
                      <div className="language-item-check">‚úì</div>
                    )}
                  </button>
                ))}
              </div>
            </div>
          )}

          {activeTab === 'appearance' && (
            <div className="settings-section">
              <h3>{t('appearance')}</h3>
              <div className="appearance-options">
                <div className="theme-toggle">
                  <div className="theme-info">
                    <div className="theme-name">{isDarkMode ? t('darkMode') : t('lightMode')}</div>
                    <div className="theme-description">
                      {isDarkMode ? 'Switch to light mode' : 'Switch to dark mode'}
                    </div>
                  </div>
                  <button
                    className={`theme-toggle-btn ${isDarkMode ? 'dark' : 'light'}`}
                    onClick={onDarkModeToggle}
                  >
                    <div className="toggle-slider"></div>
                  </button>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'email' && (
            <div className="settings-section">
              <h3>{t('changeEmail')}</h3>
              <div className="form-group">
                <label>{t('email')}</label>
                <input
                  type="email"
                  value={newEmail}
                  onChange={(e) => setNewEmail(e.target.value)}
                  placeholder="Enter new email"
                />
              </div>
              <div className="settings-actions">
                <button
                  className="settings-save-btn"
                  onClick={handleEmailSave}
                  disabled={isLoading || !newEmail}
                >
                  {isLoading ? 'Saving...' : t('save')}
                </button>
                <button className="settings-cancel-btn" onClick={onClose}>
                  {t('cancel')}
                </button>
              </div>
            </div>
          )}

          {activeTab === 'password' && (
            <div className="settings-section">
              <h3>{t('changePassword')}</h3>
              <div className="form-group">
                <label>{t('currentPassword')}</label>
                <input
                  type="password"
                  value={currentPassword}
                  onChange={(e) => setCurrentPassword(e.target.value)}
                  placeholder="Enter current password"
                />
              </div>
              <div className="form-group">
                <label>{t('newPassword')}</label>
                <input
                  type="password"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  placeholder="Enter new password"
                />
              </div>
              <div className="form-group">
                <label>{t('confirmPassword')}</label>
                <input
                  type="password"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  placeholder="Confirm new password"
                />
              </div>
              <div className="settings-actions">
                <button
                  className="settings-save-btn"
                  onClick={handlePasswordSave}
                  disabled={isLoading || !currentPassword || !newPassword || !confirmPassword}
                >
                  {isLoading ? 'Saving...' : t('save')}
                </button>
                <button className="settings-cancel-btn" onClick={onClose}>
                  {t('cancel')}
                </button>
              </div>
            </div>
          )}
        </div>

        {message && (
          <div className={`settings-message ${message.includes('error') ? 'error' : 'success'}`}>
            {message}
          </div>
        )}
      </div>
    </div>
  );
};

export default Settings;
