import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import ReactMarkdown from 'react-markdown';
import Login from './Login';
import Register from './Register';
import LanguageSelector from './LanguageSelector';
import Settings from './Settings';
import { languages, Language, getTranslation } from './languages';
import './App.css';

interface Message {
  id: string;
  text: string;
  isUser: boolean;
  timestamp: Date;
  image?: string;
  isPrescription?: boolean;
}

interface ChatSession {
  id: number;
  session_name: string;
  created_at: string;
  updated_at: string;
  message_count: number;
}

interface User {
  id: number;
  email: string;
  full_name: string;
}

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoginLoading, setIsLoginLoading] = useState(false);
  const [loginError, setLoginError] = useState('');
  const [showRegister, setShowRegister] = useState(false);
  const [isRegisterLoading, setIsRegisterLoading] = useState(false);
  const [registerError, setRegisterError] = useState('');
  const [currentLanguage, setCurrentLanguage] = useState<Language>(languages[0]); // Default to English
  const [showLanguageSelector, setShowLanguageSelector] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [isFirstTimeUser, setIsFirstTimeUser] = useState(false);
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputText, setInputText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [selectedImage, setSelectedImage] = useState<File | null>(null);
  const [showPrescriptionForm, setShowPrescriptionForm] = useState(false);
  const [patientAge, setPatientAge] = useState('');
  const [patientConditions, setPatientConditions] = useState('');
  const [isPrescriptionLoading, setIsPrescriptionLoading] = useState(false);
  const [chatSessions, setChatSessions] = useState<ChatSession[]>([]);
  const [currentSessionId, setCurrentSessionId] = useState<number | null>(null);
  const [currentSessionName, setCurrentSessionName] = useState<string>('');
  const [user, setUser] = useState<User | null>(null);
  const [showSessionSelector, setShowSessionSelector] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // Configure axios defaults
  useEffect(() => {
    const token = localStorage.getItem('access_token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      setIsAuthenticated(true);
      fetchUserInfo();
      fetchChatSessions();
    }
  }, []);

  // Initialize language and check for first-time user
  useEffect(() => {
    const savedLanguage = localStorage.getItem('user_language');
    if (savedLanguage) {
      const language = languages.find(lang => lang.code === savedLanguage);
      if (language) {
        setCurrentLanguage(language);
      }
    } else if (isAuthenticated) {
      // Show language selector for first-time authenticated users
      setIsFirstTimeUser(true);
      setShowLanguageSelector(true);
    }
  }, [isAuthenticated]);

  const fetchUserInfo = async () => {
    try {
      // For now, we'll get user info from the login response
      // In a real app, you'd have a /me endpoint
      const userInfo = localStorage.getItem('user_info');
      if (userInfo) {
        setUser(JSON.parse(userInfo));
      }
    } catch (error) {
      console.error('Error fetching user info:', error);
    }
  };

  const fetchChatSessions = async () => {
    try {
      const response = await axios.get('http://localhost:8000/chat/sessions');
      setChatSessions(response.data);
    } catch (error) {
      console.error('Error fetching chat sessions:', error);
    }
  };

  const handleLogin = async (email: string, password: string) => {
    setIsLoginLoading(true);
    setLoginError('');

    try {
      const formData = new FormData();
      formData.append('email', email);
      formData.append('password', password);

      const response = await axios.post('http://localhost:8000/auth/login', formData);

      const { access_token, user: userInfo } = response.data;

      // Store token and user info
      localStorage.setItem('access_token', access_token);
      localStorage.setItem('user_info', JSON.stringify(userInfo));

      // Set axios default header
      axios.defaults.headers.common['Authorization'] = `Bearer ${access_token}`;

      setIsAuthenticated(true);
      setUser(userInfo);
      setLoginError('');

      // Fetch chat sessions
      await fetchChatSessions();

    } catch (error: any) {
      console.error('Login error:', error);
      setLoginError(error.response?.data?.detail || 'Login failed. Please try again.');
    }

    setIsLoginLoading(false);
  };

  const handleRegister = async (email: string, password: string, fullName: string) => {
    setIsRegisterLoading(true);
    setRegisterError('');

    try {
      const formData = new FormData();
      formData.append('email', email);
      formData.append('password', password);
      formData.append('full_name', fullName);

      const response = await axios.post('http://localhost:8000/auth/register', formData);

      const { access_token, user: userInfo } = response.data;

      // Store token and user info
      localStorage.setItem('access_token', access_token);
      localStorage.setItem('user_info', JSON.stringify(userInfo));

      // Set axios default header
      axios.defaults.headers.common['Authorization'] = `Bearer ${access_token}`;

      setIsAuthenticated(true);
      setUser(userInfo);
      setRegisterError('');
      setShowRegister(false);

      // Check if this is a first-time user
      const savedLanguage = localStorage.getItem('user_language');
      if (!savedLanguage) {
        setIsFirstTimeUser(true);
        setShowLanguageSelector(true);
      }

      // Fetch chat sessions
      await fetchChatSessions();

    } catch (error: any) {
      console.error('Registration error:', error);
      setRegisterError(error.response?.data?.detail || 'Registration failed. Please try again.');
    }

    setIsRegisterLoading(false);
  };

  const handleLanguageSelect = (language: Language) => {
    setCurrentLanguage(language);
    localStorage.setItem('user_language', language.code);
    setShowLanguageSelector(false);
    setIsFirstTimeUser(false);
  };

  const handleLanguageChange = (language: Language) => {
    setCurrentLanguage(language);
    localStorage.setItem('user_language', language.code);
  };

  const handleSettingsClose = () => {
    setShowSettings(false);
  };

  const handleEmailChange = (newEmail: string) => {
    // Update user email (in a real app, this would call an API)
    if (user) {
      const updatedUser = { ...user, email: newEmail };
      setUser(updatedUser);
      localStorage.setItem('user_info', JSON.stringify(updatedUser));
    }
  };

  const handlePasswordChange = (currentPassword: string, newPassword: string) => {
    // Update password (in a real app, this would call an API)
    console.log('Password change requested');
  };

  // Translation helper function
  const t = (key: string) => getTranslation(currentLanguage.code, key);

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('user_info');
    delete axios.defaults.headers.common['Authorization'];

    setIsAuthenticated(false);
    setUser(null);
    setMessages([]);
    setInputText('');
    setSelectedImage(null);
    setShowPrescriptionForm(false);
    setChatSessions([]);
    setCurrentSessionId(null);
    setCurrentSessionName('');
  };

  const createNewSession = async () => {
    try {
      const formData = new FormData();
      formData.append('session_name', 'New Chat');

      const response = await axios.post('http://localhost:8000/chat/sessions', formData);
      const newSession = response.data;

      setCurrentSessionId(newSession.id);
      setCurrentSessionName(newSession.session_name);
      setMessages([]);
      setShowSessionSelector(false);

      // Refresh sessions list
      await fetchChatSessions();

    } catch (error) {
      console.error('Error creating new session:', error);
    }
  };

  const loadSession = async (sessionId: number, sessionName: string) => {
    try {
      const response = await axios.get(`http://localhost:8000/chat/sessions/${sessionId}/messages`);
      const sessionMessages = response.data.map((msg: any) => ({
        id: msg.id.toString(),
        text: msg.content,
        isUser: msg.is_user,
        timestamp: new Date(msg.created_at),
        isPrescription: msg.is_prescription
      }));

      setMessages(sessionMessages);
      setCurrentSessionId(sessionId);
      setCurrentSessionName(sessionName);
      setShowSessionSelector(false);

    } catch (error) {
      console.error('Error loading session:', error);
    }
  };

  const sendMessage = async () => {
    if (!inputText.trim() && !selectedImage) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      text: inputText,
      isUser: true,
      timestamp: new Date(),
      image: selectedImage ? URL.createObjectURL(selectedImage) : undefined
    };

    setMessages(prev => [...prev, userMessage]);
    setIsLoading(true);

    try {
      const formData = new FormData();
      formData.append('message', inputText);
      if (selectedImage) {
        formData.append('image', selectedImage);
      }
      if (currentSessionId) {
        formData.append('session_id', currentSessionId.toString());
      }

      const response = await axios.post('http://localhost:8000/chat/', formData);

      const aiMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: response.data.response,
        isUser: false,
        timestamp: new Date(),
        isPrescription: response.data.is_prescription_query
      };

      setMessages(prev => [...prev, aiMessage]);

      // Update session info if this is a new session
      if (response.data.session_id && !currentSessionId) {
        setCurrentSessionId(response.data.session_id);
        setCurrentSessionName(response.data.session_name);
        await fetchChatSessions();
      }

    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: 'Sorry, there was an error processing your request. Please try again.',
        isUser: false,
        timestamp: new Date(),
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
      setInputText('');
      setSelectedImage(null);
    }
  };

  const analyzePrescription = async () => {
    if (!selectedImage) return;

    setIsPrescriptionLoading(true);

    try {
      const formData = new FormData();
      formData.append('image', selectedImage);
      if (patientAge) formData.append('patient_age', patientAge);
      if (patientConditions) formData.append('patient_conditions', patientConditions);
      if (currentSessionId) {
        formData.append('session_id', currentSessionId.toString());
      }

      const response = await axios.post('http://localhost:8000/prescription-analysis/', formData);

      const userMessage: Message = {
        id: Date.now().toString(),
        text: `Prescription Analysis Request${patientAge ? ` (Age: ${patientAge})` : ''}${patientConditions ? ` (Conditions: ${patientConditions})` : ''}`,
        isUser: true,
        timestamp: new Date(),
        image: URL.createObjectURL(selectedImage),
        isPrescription: true
      };

      const aiMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: response.data.response,
        isUser: false,
        timestamp: new Date(),
        isPrescription: true
      };

      setMessages(prev => [...prev, userMessage, aiMessage]);
      setShowPrescriptionForm(false);
      setSelectedImage(null);
      setPatientAge('');
      setPatientConditions('');

      // Update session info if this is a new session
      if (response.data.session_id && !currentSessionId) {
        setCurrentSessionId(response.data.session_id);
        setCurrentSessionName(response.data.session_name);
        await fetchChatSessions();
      }

    } catch (error) {
      console.error('Error analyzing prescription:', error);
      const errorMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: 'Sorry, there was an error analyzing the prescription. Please try again or consult with a pharmacist.',
        isUser: false,
        timestamp: new Date(),
        isPrescription: true
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsPrescriptionLoading(false);
    }
  };

  const handleImageSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedImage(file);
    }
  };

  const removeSelectedImage = () => {
    setSelectedImage(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const handleKeyPress = (event: React.KeyboardEvent) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      sendMessage();
    }
  };

  if (!isAuthenticated) {
    if (showRegister) {
      return (
        <Register
          onRegister={handleRegister}
          onSwitchToLogin={() => setShowRegister(false)}
          isLoading={isRegisterLoading}
          error={registerError}
          currentLanguage={currentLanguage}
          t={t}
        />
      );
    }

    return (
      <Login 
        onLogin={handleLogin}
        onSwitchToRegister={() => setShowRegister(true)}
        isLoading={isLoginLoading}
        error={loginError}
        currentLanguage={currentLanguage}
        t={t}
      />
    );
  }

  return (
    <div className="App">
      <div className="chat-container">
        <div className="chat-header">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div>
              <h1>{t('loginTitle')}</h1>
              <p>Ask me about your child's health concerns & medication safety</p>
              {user && (
                <p style={{ fontSize: '12px', opacity: 0.8, marginTop: '5px' }}>
                  Welcome, {user.full_name}
                </p>
              )}
            </div>
            <div style={{ display: 'flex', gap: '10px' }}>
              <button 
                onClick={() => setShowLanguageSelector(true)}
                style={{
                  background: 'rgba(255, 255, 255, 0.1)',
                  border: '1px solid rgba(255, 255, 255, 0.2)',
                  color: 'white',
                  padding: '8px 16px',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontSize: '12px',
                  transition: 'all 0.2s'
                }}
                onMouseOver={(e) => {
                  e.currentTarget.style.background = 'rgba(255, 255, 255, 0.2)';
                }}
                onMouseOut={(e) => {
                  e.currentTarget.style.background = 'rgba(255, 255, 255, 0.1)';
                }}
              >
                üåç {currentLanguage.nativeName}
              </button>
              <button 
                onClick={() => setShowSettings(true)}
                style={{
                  background: 'rgba(255, 255, 255, 0.1)',
                  border: '1px solid rgba(255, 255, 255, 0.2)',
                  color: 'white',
                  padding: '8px 16px',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontSize: '12px',
                  transition: 'all 0.2s'
                }}
                onMouseOver={(e) => {
                  e.currentTarget.style.background = 'rgba(255, 255, 255, 0.2)';
                }}
                onMouseOut={(e) => {
                  e.currentTarget.style.background = 'rgba(255, 255, 255, 0.1)';
                }}
              >
                ‚öôÔ∏è Settings
              </button>
              <button 
                onClick={() => setShowSessionSelector(!showSessionSelector)}
                style={{
                  background: 'rgba(255, 255, 255, 0.1)',
                  border: '1px solid rgba(255, 255, 255, 0.2)',
                  color: 'white',
                  padding: '8px 16px',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontSize: '12px',
                  transition: 'all 0.2s'
                }}
                onMouseOver={(e) => {
                  e.currentTarget.style.background = 'rgba(255, 255, 255, 0.2)';
                }}
                onMouseOut={(e) => {
                  e.currentTarget.style.background = 'rgba(255, 255, 255, 0.1)';
                }}
              >
                üí¨ Sessions
              </button>
              <button 
                onClick={() => setShowPrescriptionForm(!showPrescriptionForm)}
                style={{
                  background: 'rgba(255, 255, 255, 0.1)',
                  border: '1px solid rgba(255, 255, 255, 0.2)',
                  color: 'white',
                  padding: '8px 16px',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontSize: '12px',
                  transition: 'all 0.2s'
                }}
                onMouseOver={(e) => {
                  e.currentTarget.style.background = 'rgba(255, 255, 255, 0.2)';
                }}
                onMouseOut={(e) => {
                  e.currentTarget.style.background = 'rgba(255, 255, 255, 0.1)';
                }}
              >
                üìã Prescription Analysis
              </button>
              <button 
                onClick={handleLogout}
                style={{
                  background: 'rgba(255, 255, 255, 0.1)',
                  border: '1px solid rgba(255, 255, 255, 0.2)',
                  color: 'white',
                  padding: '8px 16px',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontSize: '12px',
                  transition: 'all 0.2s'
                }}
                onMouseOver={(e) => {
                  e.currentTarget.style.background = 'rgba(255, 255, 255, 0.2)';
                }}
                onMouseOut={(e) => {
                  e.currentTarget.style.background = 'rgba(255, 255, 255, 0.1)';
                }}
              >
                Logout
              </button>
            </div>
          </div>

          {currentSessionName && (
            <div style={{ marginTop: '10px', textAlign: 'left' }}>
              <span style={{ 
                background: 'rgba(255, 255, 255, 0.1)', 
                padding: '4px 8px', 
                borderRadius: '4px',
                fontSize: '12px'
              }}>
                Current Session: {currentSessionName}
              </span>
            </div>
          )}
        </div>

        {showSessionSelector && (
          <div className="session-selector">
            <div style={{ 
              background: '#2d2d3f', 
              padding: '20px', 
              borderBottom: '1px solid #3a3a4f',
              maxHeight: '300px',
              overflowY: 'auto'
            }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
                <h3 style={{ color: '#ffffff', margin: 0 }}>Chat Sessions</h3>
                <button 
                  onClick={createNewSession}
                  style={{
                    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                    color: 'white',
                    border: 'none',
                    padding: '8px 16px',
                    borderRadius: '6px',
                    cursor: 'pointer',
                    fontSize: '12px'
                  }}
                >
                  + New Session
                </button>
              </div>

              {chatSessions.length === 0 ? (
                <p style={{ color: '#b8b8b8', textAlign: 'center' }}>No chat sessions yet. Start a new conversation!</p>
              ) : (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                  {chatSessions.map((session) => (
                    <div 
                      key={session.id}
                      onClick={() => loadSession(session.id, session.session_name)}
                      style={{
                        background: session.id === currentSessionId ? 'rgba(102, 126, 234, 0.2)' : '#3a3a4f',
                        padding: '12px',
                        borderRadius: '8px',
                        cursor: 'pointer',
                        border: session.id === currentSessionId ? '1px solid #667eea' : '1px solid #4a4a5f',
                        transition: 'all 0.2s'
                      }}
                      onMouseOver={(e) => {
                        if (session.id !== currentSessionId) {
                          e.currentTarget.style.background = '#4a4a5f';
                        }
                      }}
                      onMouseOut={(e) => {
                        if (session.id !== currentSessionId) {
                          e.currentTarget.style.background = '#3a3a4f';
                        }
                      }}
                    >
                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <div>
                          <div style={{ color: '#ffffff', fontWeight: '500', fontSize: '14px' }}>
                            {session.session_name}
                          </div>
                          <div style={{ color: '#b8b8b8', fontSize: '12px', marginTop: '4px' }}>
                            {session.message_count} messages ‚Ä¢ {new Date(session.updated_at).toLocaleDateString()}
                          </div>
                        </div>
                        {session.id === currentSessionId && (
                          <span style={{ color: '#667eea', fontSize: '12px' }}>‚úì Active</span>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}

        <div className="messages-container">
          {messages.map((message) => (
            <div key={message.id} className={`message ${message.isUser ? 'user' : 'ai'}`}>
              <div className="message-content">
                {message.isUser ? (
                  <p>{message.text}</p>
                ) : (
                  <div className="markdown-content">
                    <ReactMarkdown 
                      components={{
                        h1: ({node, ...props}) => <h1 style={{fontSize: '1.2em', margin: '8px 0'}} {...props} />,
                        h2: ({node, ...props}) => <h2 style={{fontSize: '1.1em', margin: '8px 0'}} {...props} />,
                        h3: ({node, ...props}) => <h3 style={{fontSize: '1em', margin: '6px 0'}} {...props} />,
                        p: ({node, ...props}) => <p style={{margin: '4px 0'}} {...props} />,
                        ul: ({node, ...props}) => <ul style={{margin: '8px 0', paddingLeft: '20px'}} {...props} />,
                        ol: ({node, ...props}) => <ol style={{margin: '8px 0', paddingLeft: '20px'}} {...props} />,
                        li: ({node, ...props}) => <li style={{margin: '2px 0'}} {...props} />,
                        strong: ({node, ...props}) => <strong style={{fontWeight: 'bold'}} {...props} />,
                        em: ({node, ...props}) => <em style={{fontStyle: 'italic'}} {...props} />,
                        blockquote: ({node, ...props}) => (
                          <blockquote style={{
                            borderLeft: '3px solid #667eea',
                            paddingLeft: '12px',
                            margin: '8px 0',
                            fontStyle: 'italic'
                          }} {...props} />
                        ),
                        code: ({node, ...props}) => (
                          <code style={{
                            backgroundColor: 'rgba(102, 126, 234, 0.1)',
                            padding: '2px 4px',
                            borderRadius: '3px',
                            fontFamily: 'monospace'
                          }} {...props} />
                        )
                      }}
                    >
                      {message.text}
                    </ReactMarkdown>
                  </div>
                )}
                {message.image && (
                  <img 
                    src={message.image} 
                    alt="Uploaded" 
                    className="message-image"
                    style={{ maxWidth: '200px', maxHeight: '200px' }}
                  />
                )}
              </div>
              <div className="message-timestamp">
                {message.timestamp.toLocaleTimeString()}
              </div>
            </div>
          ))}
          {isLoading && (
            <div className="message ai">
              <div className="message-content">
                <p>Thinking...</p>
              </div>
            </div>
          )}
          {isPrescriptionLoading && (
            <div className="message ai">
              <div className="message-content">
                <p>Analyzing prescription...</p>
              </div>
            </div>
          )}
        </div>

        {showPrescriptionForm && (
          <div className="prescription-form">
            <h3 style={{ color: '#ffffff', marginBottom: '15px' }}>üìã Prescription Analysis</h3>
            <div style={{ display: 'flex', gap: '15px', marginBottom: '15px' }}>
              <div style={{ flex: 1 }}>
                <label style={{ color: '#e0e0e0', fontSize: '14px', display: 'block', marginBottom: '5px' }}>
                  Patient Age (optional)
                </label>
                <input
                  type="text"
                  value={patientAge}
                  onChange={(e) => setPatientAge(e.target.value)}
                  placeholder="e.g., 5 years old"
                  style={{
                    width: '100%',
                    padding: '8px 12px',
                    border: '2px solid #3a3a4f',
                    borderRadius: '6px',
                    background: '#2d2d3f',
                    color: '#e0e0e0',
                    fontSize: '14px'
                  }}
                />
              </div>
              <div style={{ flex: 1 }}>
                <label style={{ color: '#e0e0e0', fontSize: '14px', display: 'block', marginBottom: '5px' }}>
                  Medical Conditions (optional)
                </label>
                <input
                  type="text"
                  value={patientConditions}
                  onChange={(e) => setPatientConditions(e.target.value)}
                  placeholder="e.g., asthma, allergies"
                  style={{
                    width: '100%',
                    padding: '8px 12px',
                    border: '2px solid #3a3a4f',
                    borderRadius: '6px',
                    background: '#2d2d3f',
                    color: '#e0e0e0',
                    fontSize: '14px'
                  }}
                />
              </div>
            </div>
            <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleImageSelect}
                accept="image/*"
                style={{ display: 'none' }}
              />
              <button 
                onClick={() => fileInputRef.current?.click()}
                style={{
                  background: '#2d2d3f',
                  color: '#667eea',
                  border: '2px solid #3a3a4f',
                  padding: '8px 16px',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '14px'
                }}
              >
                üì∑ Upload Prescription
              </button>
              {selectedImage && (
                <button 
                  onClick={analyzePrescription}
                  disabled={isPrescriptionLoading}
                  style={{
                    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                    color: 'white',
                    border: 'none',
                    padding: '8px 16px',
                    borderRadius: '6px',
                    cursor: 'pointer',
                    fontSize: '14px'
                  }}
                >
                  {isPrescriptionLoading ? 'Analyzing...' : 'Analyze Prescription'}
                </button>
              )}
            </div>
            {selectedImage && (
              <div style={{ marginTop: '10px', display: 'flex', alignItems: 'center', gap: '10px' }}>
                <img 
                  src={URL.createObjectURL(selectedImage)} 
                  alt="Selected" 
                  style={{ maxWidth: '80px', maxHeight: '80px', borderRadius: '4px' }}
                />
                <button 
                  onClick={removeSelectedImage}
                  style={{
                    background: '#ff4757',
                    color: 'white',
                    border: 'none',
                    borderRadius: '50%',
                    width: '20px',
                    height: '20px',
                    cursor: 'pointer',
                    fontSize: '10px'
                  }}
                >
                  ‚úï
                </button>
              </div>
            )}
          </div>
        )}

        <div className="input-container">
          <div className="input-wrapper">
            <textarea
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder={t('chatPlaceholder')}
              className="text-input"
              rows={3}
            />
            <div className="input-actions">
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleImageSelect}
                accept="image/*"
                style={{ display: 'none' }}
              />
              <button 
                onClick={() => fileInputRef.current?.click()}
                className="upload-btn"
                type="button"
              >
                üì∑
              </button>
              <button 
                onClick={sendMessage}
                disabled={isLoading || (!inputText.trim() && !selectedImage)}
                className="send-btn"
              >
                {t('send')}
              </button>
            </div>
          </div>

          {selectedImage && !showPrescriptionForm && (
            <div className="selected-image">
              <img 
                src={URL.createObjectURL(selectedImage)} 
                alt="Selected" 
                style={{ maxWidth: '100px', maxHeight: '100px' }}
              />
              <button onClick={removeSelectedImage} className="remove-image-btn">
                ‚úï
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Language Selector Modal */}
      <LanguageSelector
        onLanguageSelect={handleLanguageSelect}
        isVisible={showLanguageSelector}
      />

      {/* Settings Modal */}
      <Settings
        isVisible={showSettings}
        onClose={handleSettingsClose}
        currentLanguage={currentLanguage}
        onLanguageChange={handleLanguageChange}
        currentEmail={user?.email || ''}
        onEmailChange={handleEmailChange}
        onPasswordChange={handlePasswordChange}
      />
    </div>
  );
}

export default App;
